mapper.select:
SELECT a.*, b.mainImg, ( SELECT COUNT(*) FROM `product` AS a LEFT JOIN `productimg` AS b ON a.pno = b.pno LEFT JOIN `seller` AS c ON a.sid = c.uid
a.pname like CONCAT('%','${keyword}','%') a.company like CONCAT('%','${keyword}','%') a.seller like CONCAT('%','${keyword}','%') a.pno=${keyword}
) as total FROM `product` AS a LEFT JOIN `productimg` AS b ON a.pno = b.pno LEFT JOIN `seller` AS c ON a.sid = c.uid
a.pname like CONCAT('%','${keyword}','%') a.company like CONCAT('%','${keyword}','%') a.seller like CONCAT('%','${keyword}','%') a.pno=${keyword}
ORDER BY a.rdate DESC, a.pno ASC LIMIT ${size} OFFSET ${(pg - 1) * size}
