mapper.select:
SELECT p.*, img.mainImg, img.subImg, img.detailImg
FROM product p
LEFT JOIN productimg img ON p.pno = img.pno
WHERE 1=1

    AND p.pname LIKE CONCAT('%', #{search}, '%')


    AND p.price >= #{minPrice}


    AND p.price <= #{maxPrice}


    AND p.cate = #{category}

ORDER BY p.rdate DESC

SELECT COUNT(*)
FROM product p
LEFT JOIN productimg img ON p.pno = img.pno
WHERE 1=1

    AND p.pname LIKE CONCAT('%', #{search}, '%')


    AND p.price >= #{minPrice}


    AND p.price <= #{maxPrice}


    AND p.cate = #{category}
